package basicJava;

public class OperEX7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int res = 0;
		res += a; System.out.println("res = "+res);
		res *= a; System.out.println("res = "+res);
		res -= a; System.out.println("res = "+res);
		res %= a; System.out.println("res = "+res);
	}

}
