package ex04control;

public class ForEx3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] arr = {"aa", "bb", "cc", "dd", "ee"};
		
		for(String s : arr) {
			System.out.println("Value of array ? "+s);
				
		}
		for(int i=0; i<arr.length; i++) {
			System.out.println("Value if array ? " +arr[i]);
		}

	}

}
